SELECT

FROM sys_office a


    a.del_flag = #{DEL_FLAG_NORMAL}

        and a.code = #{code}


        and a.type = #{type}



${sqlMap.dsf}
ORDER BY a.code

SELECT

FROM sys_office a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}
