mapper.update:
UPDATE sys_office SET
del_flag = #{DEL_FLAG_DELETE},
update_date = #{updateDate}
WHERE id = #{id}

update.foreach:
UPDATE sys_office SET
del_flag = #{item.DEL_FLAG_DELETE},
update_date = #{updateDate}
WHERE id = #{item.id}

mapper.select:
SELECT

FROM sys_office a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.area_id = #{area.id}


        AND a.type = #{type}


        AND a.name LIKE concat('%',#{name},'%')



${sqlMap.dsf}


        ORDER BY ${page.orderBy}


        ORDER BY a.code
