mapper.select:
SELECT

FROM sys_role a

WHERE a.id = #{id}

SELECT

FROM sys_role a
WHERE a.name = #{name} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT

FROM sys_role a
WHERE a.enname = #{enname} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT 

FROM sys_role a

WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND a.useable=#{useable}

    AND u.id = #{user.id}


    AND o.id = #{office.id}


    AND u.login_name = #{user.loginName}


${sqlMap.dsf}
ORDER BY o.code, a.name

mapper.insert:
INSERT INTO sys_role_menu(role_id, menu_id)

    SELECT #{id}, #{menu.id}

        FROM dual

INSERT INTO sys_role_office(role_id, office_id)

    SELECT #{id}, #{office.id}

        FROM dual

mapper.update:
UPDATE sys_role SET
del_flag = #{DEL_FLAG_DELETE},
update_date = #{updateDate}
WHERE id = #{id}
